import React, { useState, useEffect } from 'react';
import { VictoryChart, VictoryBar, VictoryAxis, VictoryTooltip } from 'victory';

import axios from 'axios';
import './styles/ScoreDistribution.css';

const ScoreDistribution = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8000/api/result')
      .then(response => {
        const scoresByTopic = groupScoresByTopic(response.data);
        const histogramData = calculateHistogramData(scoresByTopic);
        setData(histogramData);
        setIsLoading(false);
      })
      .catch(error => {
        console.log(error);
        setIsLoading(false);
      });
  }, []);

  const groupScoresByTopic = (data) => {
    const scoresByTopic = {};
    for (const student of data) {
      const { topic, point } = student;
      if (!scoresByTopic[topic]) {
        scoresByTopic[topic] = [];
      }
      scoresByTopic[topic].push(point);
    }
    return scoresByTopic;
  };

  const calculateHistogramData = (scoresByTopic) => {
    const bins = [0, 5, 10, 15, 20, 25];
    const histogramData = [];
    for (const topic in scoresByTopic) {
      const binCounts = new Array(bins.length - 1).fill(0);
      const scores = scoresByTopic[topic];
      for (const score of scores) {
        for (let i = 1; i < bins.length; i++) {
          if (score >= bins[i-1] && score < bins[i]) {
            binCounts[i-1]++;
            break;
          }
        }
      }
      const topicHistogramData = binCounts.map((count, i) => ({ x: `${bins[i]}-${bins[i+1]}`, y: count }));
      histogramData.push({ topic, data: topicHistogramData });
    }
    return histogramData;
  };

  const getUniqueRollNumbers = (data) => {
    return new Set(data.map(student => student.roll_no)).size;
  };

  const getMaxYValue = (data) => {
    const rollNumbersCount = getUniqueRollNumbers(data);
    const maxYValue = Math.ceil(rollNumbersCount / 10) * 10;
    const tickCount = maxYValue / 5;
    return { maxYValue, tickCount };
  };

  const { maxYValue, tickCount } = getMaxYValue(data);

  if (isLoading) {
    return (
      <div className="loading">
        <p>Loading</p>
      </div>
    );
  }

  return (
<div className="score-distribution">
<h2>Topic Wise Score Distribution</h2>
<VictoryChart domainPadding={20}>
<VictoryAxis
  dependentAxis
  label="No. of Students"
  tickValues={[...Array(maxYValue / 5 + 1).keys()].map((i) => i * 5)}
  tickFormat={(t) => `${t}`}
/>
<VictoryAxis
label="Score"
style={{
axis: { stroke: '#000' },
tickLabels: { fontSize: 12, padding: 5 },
}}
/>
<VictoryBar
horizontal
data={data.map((topicData) => topicData.data)}
labels={({ datum }) => datum.y}
labelComponent={<VictoryTooltip />}
barWidth={20}
style={{
data: { fill: '#c43a31' }
}}
categories={{ x: bins.map((bin) => `${bin}-${bin+5}`) }}
/>
<VictoryAxis dependentAxis style={{ tickLabels: { fontSize: 12, padding: 5 } }} />
<VictoryLegend
x={200}
orientation="horizontal"
gutter={20}
data={data.map((topicData) => ({ name: topicData.topic }))}
