import React from 'react';
import { Doughnut } from 'react-chartjs-2';

const DoughnutChart = ({ students }) => {
  const easyCount = students.reduce((acc, student) => acc + student.easy, 0);
  const mediumCount = students.reduce((acc, student) => acc + student.medium, 0);
  const hardCount = students.reduce((acc, student) => acc + student.hard, 0);

  const data = {
    labels: ['Easy', 'Medium', 'Hard'],
    datasets: [
      {
        label: 'Difficulty Level',
        data: [easyCount, mediumCount, hardCount],
        backgroundColor: ['#36a2eb', '#ffcd56', '#ff6384']
      }
    ]
  };

  return <Doughnut data={data} />;
};

export default DoughnutChart;
