import React from 'react';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

import { Link } from 'react-router-dom';
import ScoreDistribution from './ScoreDistribution';
import PassFailRate from './PassFailRate';
import TopicwiseTopPerformers from './TopicwiseTopPerformers';
import './styles/Dashboard.css';

const generatePdf = () => {
  const input = document.querySelector('#pdf-content');

  html2canvas(input, { logging: false }).then((canvas) => {
    const imgData = canvas.toDataURL('image/png');
    const pdf = new jsPDF();
    const imgProps = pdf.getImageProperties(imgData);
    const pdfWidth = pdf.internal.pageSize.getWidth() - 20;
    const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

    let currentHeight = pdfHeight;
    let imgHeight = imgProps.height;
    let imgWidth = imgProps.width;
    let position = 0;

    while (currentHeight > 0) {
      pdf.addImage(imgData, 'PNG', 10, position, pdfWidth, imgHeight);

      currentHeight -= pdf.internal.pageSize.getHeight();
      position -= pdf.internal.pageSize.getHeight();

      if (currentHeight > 0) {
        pdf.addPage();
        imgHeight = (imgHeight * pdfWidth) / imgWidth;
        position = -10;
      }
    }

    pdf.save('dashboard.pdf');
  });
};







const Dashboard = () => {
  return (
    <div className="dashboard">

      <div id="pdf-content">
      <h1>Analysis Report</h1>
      <TopicwiseTopPerformers />
        <ScoreDistribution />
        <PassFailRate />

      </div>
      <div className="button-container">
        <Link to="/scoreboard"><button>Back</button></Link>
        <button onClick={generatePdf}>Download PDF</button>
        <Link to="/"><button>Logout</button></Link>
      </div>
    </div>
  );
};

export default Dashboard;
