import React from 'react';
import PropTypes from 'prop-types';
import './Score.css';

const Score = ({ score, totalQuestions, restartQuiz, isLastQuestion, showViewScore, onViewScoreClick }) => {
  return (
    <div className="score-section">
      <h2>Quiz Completed!</h2>
      <p>You scored {score} out of {totalQuestions}.</p>
      {isLastQuestion && !showViewScore && (
        <button className="view-score-button" onClick={onViewScoreClick}>
          View Score
        </button>
      )}
      <div className="button-container">
        <button onClick={restartQuiz}>Restart</button>
      </div>
    </div>
  );
};

Score.propTypes = {
  score: PropTypes.number.isRequired,
  totalQuestions: PropTypes.number.isRequired,
  restartQuiz: PropTypes.func.isRequired,
  isLastQuestion: PropTypes.bool.isRequired,
  showViewScore: PropTypes.bool.isRequired,
  onViewScoreClick: PropTypes.func.isRequired,
};

export default Score;
