import React, { useState } from "react";
import questionsData from "../data/questions.json";


import Question from "./Question";
import Options from "./Options";
import NextButton from "./NextButton";

const QuizPage = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOptionIndex, setSelectedOptionIndex] = useState(null);
  const [score, setScore] = useState(0);

  const handleOptionSelect = (index) => {
    setSelectedOptionIndex(index);
  };

  const handleNextClick = () => {
    const currentQuestion = questionsData[currentQuestionIndex];
    if (currentQuestion.options[selectedOptionIndex].isCorrect) {
      setScore(score + currentQuestion.difficultyLevel);
    }
    setSelectedOptionIndex(null);
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  if (currentQuestionIndex === questionsData.length) 
  {
    return <div>Quiz finished! Your score is {score}.</div>;
  }

  const currentQuestion = questionsData[currentQuestionIndex];
  const options = currentQuestion.options.map((option, index) => (
    <Options
      key={index}
      text={option.text}
      isCorrect={option.isCorrect}
      handleOptionSelect={() => handleOptionSelect(index)}
      selected={selectedOptionIndex === index}
    />
  ));

  return (
    <div>
      <Question text={currentQuestion.text} />
      {options}
      <NextButton handleNextClick={handleNextClick} />
    </div>
  );
};

export default QuizPage;
