import React, { useState, useEffect } from 'react';
import _ from 'lodash';
import { Link } from 'react-router-dom'
import './Quiz.css';

import Score from './Score';
import {quizData} from './quizData';

const Quiz = ({ questions, userAnswers, setUserAnswers }) => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);

  useEffect(() => {
    // update the score based on the latest selection
    const latestAnswer = userAnswers[currentQuestion];
    const question = questions[currentQuestion];

    if (latestAnswer !== null && latestAnswer === question.answer) {
      setScore(score + 1);
    } else if (latestAnswer !== null && latestAnswer !== question.answer) {
      setScore(score - 1);
    }
  }, [userAnswers, currentQuestion]);

  const handleAnswerOptionClick = (isCorrect) => {
    setUserAnswers((prevUserAnswers) => ({
      ...prevUserAnswers,
      [currentQuestion]: isCorrect,
    }));

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };


  const restartQuiz = () => {
    setCurrentQuestion(0);
    setShowScore(false);
    setScore(0);
    setUserAnswers({});
  };

  return (
    <div className="quiz">
      {showScore ? (
        <Score score={score} totalQuestions={questions.length} restartQuiz={restartQuiz}/>
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{questions.length}
            </div>
            <div className="question-text">{questions[currentQuestion].question}</div>
          </div>
          <div className="answer-section">
            {_.chunk(_.shuffle(questions[currentQuestion].options), 2).map((optionChunk) => (
              <div className="answer-row">
                {optionChunk.map((option) => (
                  <button onClick={() => handleAnswerOptionClick(option === questions[currentQuestion].answer)}>
                    {option}
                  </button>
                ))}
              </div>
            ))}
          </div>
          <div className="button-container">
            {currentQuestion > 0 && (
              <button onClick={() => setCurrentQuestion(currentQuestion - 1)}>Previous</button>
            )}
            {currentQuestion === questions.length - 1 ? (
              <button onClick={restartQuiz}>Finish</button>
            ) : (
              <button onClick={() => setCurrentQuestion(currentQuestion + 1)}>Next</button>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Quiz;
