import React, { useState } from 'react';
import Question from './Question';
import Options from './Options';
import NextButton from './NextButton';
import fractionQuestions from '../quizdata.json';

const QuizPage = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOptionIndex, setSelectedOptionIndex] = useState(-1);
  const [score, setScore] = useState(0);

  const handleOptionSelect = (index) => {
    setSelectedOptionIndex(index);
  };

  const handleNextClick = () => {
    if (selectedOptionIndex === -1) {
      return;
    }

    const currentQuestion = fractionQuestions[currentQuestionIndex];
    if (currentQuestion.correctOptionIndex === selectedOptionIndex) {
      setScore(score + 1);
    }

    setSelectedOptionIndex(-1);
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const currentQuestion = fractionQuestions[currentQuestionIndex];
  const isLastQuestion = currentQuestionIndex === fractionQuestions.length - 1;

  return (
    <div>
      <Question text={currentQuestion.question} />
      <Options
        options={currentQuestion.options}
        selectedOptionIndex={selectedOptionIndex}
        onOptionSelect={handleOptionSelect}
      />
      <NextButton
        disabled={selectedOptionIndex === -1}
        isLastQuestion={isLastQuestion}
        onNextClick={handleNextClick}
      />
    </div>
  );
};

export default QuizPage;
