import React, { useState } from 'react';
import './styles/Scoreboard.css';

const Scoreboard = () => {
  const [students, setStudents] = useState([
    { id: 1, name: 'John', rollNumber: '1234', score: 8 },
    { id: 2, name: 'Jane', rollNumber: '5678', score: 7 },
    { id: 3, name: 'Bob', rollNumber: '9101', score: 9 },
  ]);

  return (
    <div className="scoreboard">
      <h1>Scoreboard</h1>
      <table>
        <thead>
          <tr>
            <th>Student Name</th>
            <th>Roll Number</th>
            <th>Score</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.id}>
              <td>{student.name}</td>
              <td>{student.rollNumber}</td>
              <td>{student.score}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Scoreboard;
