import React, { useState, useEffect } from 'react';
import './styles/Scoreboard.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Scoreboard = () => {
  const [students, setStudents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [filterType, setFilterType] = useState('');
  const [filterValue, setFilterValue] = useState('');

  useEffect(() => {
    // Make an HTTP GET request to your backend API endpoint
    axios.get('http://localhost:8000/api/result')
      .then(response => {
        // Sort the students array in descending order based on point
        const sortedStudents = response.data.sort((a, b) => b.point - a.point);
        // Set the students state variable to the sorted data retrieved from the API
        setStudents(sortedStudents);
        setIsLoading(false);
      })
      .catch(error => {
        console.log(error);
        setIsLoading(false);
      });
  }, []);

  const handleDelete = () => {
    axios.delete('http://localhost:8000/api/result')
      .then(response => {
        // Reload the page to update the scoreboard with the new data
        window.location.reload();
      })
      .catch(error => {
        console.log(error);
      });
  }

  const handleFilter = () => {
    if (filterType === 'topic') {
      const filteredStudents = students.filter(student => student.topic === filterValue);
      setStudents(filteredStudents);
    } else if (filterType === 'rollNumber') {
      const filteredStudent = students.find(student => student.rollNumber === filterValue);
      if (filteredStudent) {
        setStudents([filteredStudent]);
      } else {
        setStudents([]);
      }
    }
  }

  if (isLoading) {
    return (
      <div className="loading">
        <p>Loading</p>
      </div>
    );
  }

  if (students.length === 0) {
    return (
      <div className="scoreboard-empty">
        <h1>SCORE BOARD</h1>
        <p>Scoreboard is empty!!</p>
        <div className="scoreboard-buttons">
          <Link to="/Teacher"><button>Home</button></Link>
         <Link to="/"><button>Logout</button></Link>
        </div>
      </div>
    );
  }

  return (
    <div className="scoreboard">
      <h1>SCORE BOARD</h1>
      <table>
        <thead>
          <tr>
            <th>S.No.</th>
            <th>Student Name</th>
            <th>Roll Number</th>
            <th>Score</th>
            <th>topic</th>
            <th>Result</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student, index) => (
            <tr key={student._id}>
              <td>{index + 1}</td>
              <td>{student.studentName}</td>
              <td>{student.rollNumber}</td>

              <td>{student.point}</td>
              <td>{student.topic}</td>
              <td>{student.point >= 4 ? (<td className="result-pass">Pass</td>) : (<td className="result-fail">Fail</td>)}</td>

            </tr>
          ))}
        </tbody>
      </table>

      <div className="scoreboard-buttons">
        <Link to="/Teacher"><button>Home</button></Link>
        <button onClick={handleDelete}>Delete Results</button>
        <Link to="/"><button>Logout</button></Link>
      </div>
    </div>
  );
};

export default Scoreboard;
