  useEffect(() => {
  setIsLoading(true);

  const allQuestions = [];
  let difficulty = 'easy';

  axios
    .get(`http://localhost:8000/api/questions?topic=${encodeURIComponent(topic)}`)
    .then((response) => {
      const questions = response.data;

      // filter questions based on difficulty level
      let filteredQuestions = questions.filter(q => q.difficulty === difficulty);

      // randomly select questions from the filtered list
      for (let i = 0; i < Math.min(filteredQuestions.length, 3); i++) {
        const randIndex = Math.floor(Math.random() * filteredQuestions.length);
        allQuestions.push(filteredQuestions[randIndex]);
        filteredQuestions.splice(randIndex, 1);
      }

      // shuffle the questions
      const shuffledQuestions = _.shuffle(allQuestions);

      console.log('Topic:', topic);
      console.log('Fetched Questions:', shuffledQuestions);
      setQuestions(shuffledQuestions);
      setIsLoading(false);
    })
    .catch((error) => {
      console.log(error);
      setIsLoading(false);
    });

  // Update difficulty level based on user's performance
  const handleAnswer = (isCorrect) => {
    if (isCorrect) {
      if (difficulty === 'easy') {
        difficulty = 'medium';
      } else if (difficulty === 'medium') {
        difficulty = 'hard';
      }
    } else if (difficulty === 'medium' || difficulty === 'hard') {
      difficulty = 'easy';
    }
  };

  // Pass the handleAnswer function to the child components as a prop
}, [topic]);

  const handleAnswerOptionClick = (isCorrect, difficulty) => {
    let questionScore = 0;
    if (isCorrect) {
      if (difficulty === 'easy') {
        questionScore = 1;
      } else if (difficulty === 'medium') {
        questionScore = 2;
      } else if (difficulty === 'hard') {
        questionScore = 3;
      }
      setScore(score + questionScore);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    }
    setAnsweredQuestions(answeredQuestions + 1);
    if (answeredQuestions >= 9) {
      setShowScore(true);
    }
  };

  useEffect(() => {
    console.log('Current Score:', score);
  }, [score]);

  return (
    <div className="quiz">
      {isLoading ? (
        <div className="loading">Loading</div>
      ) : showScore ? (
        <Score
          score={score}
          totalQuestions={questions.length}
          hasPostedResults={hasPostedResults}
          setHasPostedResults={setHasPostedResults}
        />
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/10
            </div>
            <div className="question-text">{questions[currentQuestion].question}</div>
            <div className="difficulty-level">
              Difficulty level: {questions[currentQuestion].difficulty}
            </div>
          </div>
          <div className="answer-section">
            {_.chunk(_.shuffle(questions[currentQuestion].options), 2).map((optionChunk) => (
              <div className="answer-row">
                {optionChunk.map((option) => (
                  <button
                    onClick={() =>
                      handleAnswerOptionClick(
                        option === questions[currentQuestion].answer,
                        questions[currentQuestion].difficulty
                      )
                    }
                  >
                    {option}
                  </button>
                ))}
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default Quiz;
