import React, { useState, useEffect, useRef } from 'react';
import _ from 'lodash';
import { Link } from 'react-router-dom';
import './styles/Quiz.css';
import Score from './Score';
import axios from 'axios';

const Quiz = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [hasPostedResults, setHasPostedResults] = useState(false);
  const [previousDifficulty, setPreviousDifficulty] = useState(null);

useEffect(() => {
  // Make an HTTP GET request to your backend API endpoint
  axios
    .get('http://localhost:8000/api/questions')
    .then((response) => {

      // Store questions in separate arrays based on difficulty level
      const easyQuestions = response.data.filter((question) => question.difficulty === 'easy');
      const mediumQuestions = response.data.filter((question) => question.difficulty === 'medium');
      const hardQuestions = response.data.filter((question) => question.difficulty === 'hard');

      // Randomly select an easy question as the first question
      const selectedEasyQuestion = _.sample(easyQuestions);

      // Set the questions state variable to the selected easy question
      setQuestions([selectedEasyQuestion]);

      // Set the current difficulty to 'easy'
      let currentDifficulty = 'easy';

      // Set the number of answered questions to 0
      let answeredQuestions = 0;

      // Set the score to 0
      setScore(0);

      // Set the current question index to 0
      setCurrentQuestion(0);

      setIsLoading(false);
    })
    .catch((error) => {
      console.log(error);
      setIsLoading(false);
    });
}, []);

  const handleAnswerOptionClick = (isCorrect, difficulty) => {
  let questionScore = 0;
  let nextDifficulty = difficulty;
  let nextQuestions = [];

  if (isCorrect) {
    if (difficulty === 'easy') {
      questionScore = 1;
      answeredQuestions += 1;
      if (answeredQuestions === 2) {
        nextDifficulty = 'medium';
        nextQuestions = _.sampleSize(mediumQuestions, 3);
      }
    } else if (difficulty === 'medium') {
      questionScore = 2;
      answeredQuestions += 1;
      if (answeredQuestions === 4) {
        nextDifficulty = 'hard';
        nextQuestions = _.sampleSize(hardQuestions, 3);
      }
    } else if (difficulty === 'hard') {
      questionScore = 3;
      answeredQuestions += 1;
    }
    setScore(score + questionScore);
  }

  const nextQuestion = currentQuestion + 1;
  if (nextQuestion < questions.length) {
    setCurrentQuestion(nextQuestion);
  } else {
    const selectedQuestions = _.shuffle(nextQuestions);
    setQuestions([...questions, ...selectedQuestions]);
    setCurrentQuestion(nextQuestion);
    nextDifficulty = 'easy';
    answeredQuestions = 0;
  }

  if (nextDifficulty !== difficulty) {
    const selectedQuestions = _.shuffle(nextQuestions);
    setQuestions([...questions, ...selectedQuestions]);
    setCurrentQuestion(nextQuestion);
    setDifficulty(nextDifficulty);
    answeredQuestions = 0;
  }
};


useEffect(() => {
  console.log('Current Score:', score);
}, [score]);

  return (
    <div className="quiz">
      {isLoading ? (
        <div className="loading">Loading</div>
      ) : showScore ? (
        <Score
          score={score}
          totalQuestions={questions.length}
          hasPostedResults={hasPostedResults}
          setHasPostedResults={setHasPostedResults}
        />
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{questions.length}
            </div>
            <div className="question-text">{questions[currentQuestion].question}</div>
            <div className="difficulty-level">
              Difficulty level: {questions[currentQuestion].difficulty}
            </div>
          </div>
          <div className="answer-section">
            {_.chunk(_.shuffle(questions[currentQuestion].options), 2).map((optionChunk) => (
              <div className="answer-row">
                {optionChunk.map((option) => (
                  <button
                    onClick={() =>
                      handleAnswerOptionClick(
                        option === questions[currentQuestion].answer,
                        questions[currentQuestion].difficulty
                      )
                    }
                  >
                    {option}
                  </button>
                ))}
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default Quiz;
