import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './styles/StudentLogin.css';

function StudentLogin() {
  const [rollNumber, setRollNumber] = useState('');
  const [studentName, setStudentName] = useState('');
  const [warning, setWarning] = useState('');
  const [rollNumberWarning, setRollNumberWarning] = useState('');
  const [studentNameWarning, setStudentNameWarning] = useState('');

  const history = useHistory();

  const handleRollNumberChange = (event) => {
    const value = event.target.value;
    if (!value.match(/^\d*$/)) {
      setWarning('Please enter only numbers for Roll Number');
      setRollNumber('');
    } else {
      setWarning('');
      setRollNumber(value);
      setRollNumberWarning('');
    }
  };

  const handleStudentNameChange = (event) => {
    const value = event.target.value;
    if (!value.match(/^[a-zA-Z ]*$/)) {
      setWarning('Please enter only letters for Student Name');
      setStudentName('');
    } else {
      setWarning('');
      setStudentName(value);
      setStudentNameWarning('');
    }
  };

  const handleLogin = () => {
    if (rollNumber.trim() === '') {
      setRollNumberWarning('Please enter a roll number');
    } else if (studentName.trim() === '') {
      setStudentNameWarning('Please enter a student name');
    } else if (rollNumberWarning !== '' || studentNameWarning !== '') {
      // Do nothing, invalid input warning already displayed
    } else {
      // Login successful, redirect to student homepage
      history.push(`/quiz?studentName=${encodeURIComponent(studentName)}&rollNumber=${encodeURIComponent(rollNumber)}`);
    }
  };

  const handleInputFocus = () => {
    setWarning('');
    setRollNumberWarning('');
    setStudentNameWarning('');
  };

  return (
    <div className="wrapper">
  <form className="form">
    <h1 className="title">Login</h1>
    <div className="input-group">
      <label className="label">Roll Number:</label>
      <input className="input" type="text" value={rollNumber} onChange={handleRollNumberChange} onFocus={handleInputFocus} />
      {rollNumberWarning && <p className="warning">{rollNumberWarning}</p>}
      {warning && <p className="warning">{warning}</p>}
    </div>
    <div className="input-group">
      <label className="label">Student Name:</label>
      <input className="input" type="text" value={studentName} onChange={handleStudentNameChange} onFocus={handleInputFocus} />
      {studentNameWarning && <p className="warning">{studentNameWarning}</p>}
      {warning && <p className="warning">{warning}</p>}
    </div>
    <button className="button" type="button" onClick={handleLogin}>
      Attempt Quiz!
    </button>
    {rollNumberWarning === '' && studentNameWarning === '' && warning !== '' && <p className="warning-attempt">{warning}</p>}
  </form>
</div>

  );
}

export default StudentLogin;
