import React, { useState } from 'react';
import _ from 'lodash';
import { Link } from 'react-router-dom'
import './Quiz.css';

import Score from './Score';
import {quizData} from './quizData';

const Quiz = () => {
  const [questions, setQuestions] = useState(_.shuffle(quizData));
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);
  const [answered, setAnswered] = useState(false);

  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    setAnswered(true);
  };

  const handleNextButtonClick = () => {
    setCurrentQuestion(currentQuestion + 1);
    setAnswered(false);
  };

  const handlePrevButtonClick = () => {
    setCurrentQuestion(currentQuestion - 1);
    setAnswered(false);
  };

  const handleFinishButtonClick = () => {
    setShowScore(true);
  };

  const restartQuiz = () => {
    setQuestions(_.shuffle(quizData));
    setCurrentQuestion(0);
    setShowScore(false);
    setScore(0);
  };

  return (
    <div className="quiz">
      {showScore ? (
        <Score score={score} totalQuestions={questions.length} restartQuiz={restartQuiz}/>
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{questions.length}
            </div>
            <div className="question-text">{questions[currentQuestion].question}</div>
          </div>
          <div className="answer-section">
            {_.chunk(_.shuffle(questions[currentQuestion].options), 2).map((optionChunk, index) => (
              <div className="answer-row" key={index}>
                {optionChunk.map((option, optionIndex) => (
                  <button
                    key={optionIndex}
                    onClick={() => handleAnswerOptionClick(option === questions[currentQuestion].answer)}
                    disabled={answered}
                  >
                    {option}
                  </button>
                ))}
              </div>
            ))}
          </div>
          <div className="button-section">
            <button onClick={handlePrevButtonClick} disabled={currentQuestion === 0}>
              Previous
            </button>
            {currentQuestion < questions.length - 1 ? (
              <button onClick={handleNextButtonClick} disabled={!answered}>
                Next
              </button>
            ) : (
              <button onClick={handleFinishButtonClick} disabled={!answered}>
                Finish
              </button>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Quiz;
