import React, { useState } from "react";
import { quizData } from "./quizData";
import './Quiz.css';


const Quiz = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);

  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < quizData.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  return (
    <div className="quiz-container">
  <h1 className="quiz-header">{this.state.quiz.name}</h1>
  {currentQuestion && (
    <>
      <p className="quiz-question">{currentQuestion.question}</p>
      <div className="quiz-options">
        {currentQuestion.options.map((option, index) => (
          <div className="quiz-option" key={index}>
            <input
              type="radio"
              id={index}
              name="option"
              value={option}
              onChange={this.handleOptionChange}
              checked={selectedOption === option}
            />
            <label htmlFor={index} className="quiz-option-label">{option}</label>
          </div>
        ))}
      </div>
      <button className="quiz-next-button" onClick={this.handleNextQuestion}>
        {isLastQuestion ? 'Finish' : 'Next'}
      </button>
    </>
  )}
</div>
  );
}

export default Quiz;
