import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import './styles/StudentLogin.css';

function StudentLogin() {
  const [rollNumber, setRollNumber] = useState('');
  const [studentName, setStudentName] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const history = useHistory();

  const handleRollNumberChange = (event) => {
    setRollNumber(event.target.value);
  };

  const handleStudentNameChange = (event) => {
    setStudentName(event.target.value);
  };

 const handleLogin = () => {
  if (rollNumber.trim() !== '' && studentName.trim() !== '') {
    // Check if roll number exists in API
    axios.get(`http://localhost:8000/api/result/${rollNumber}`)
      .then(response => {
        // Roll number exists, display error message
        setErrorMessage(`Roll number ${rollNumber} already exists`);
      })
      .catch(error => {
        if (error.response && error.response.status === 404) {
          // Roll number doesn't exist, add to API and redirect to quiz page
          axios.post('http://localhost:8000/api/result', {
            rollnumber: rollNumber,
            studentname: studentName,
            point: 0
          })
          .then(response => {
            history.push(`/quiz?studentName=${encodeURIComponent(studentName)}&rollNumber=${encodeURIComponent(rollNumber)}`);
          })
          .catch(error => {
            if (error.response && error.response.status === 409) {
              // Roll number already exists, display error message
              setErrorMessage(`Roll number ${rollNumber} already exists`);
            } else {
              // Some other error occurred, display generic error message
              setErrorMessage('An error occurred, please try again later');
            }
          });
        } else {
          // Some other error occurred, display generic error message
          setErrorMessage('An error occurred, please try again later');
        }
      });
  } else {
    // Login failed, clear input fields and display error message
    setRollNumber('');
    setStudentName('');
    setErrorMessage('Please fill in all fields');
  }
};


  return (
    <div className="wrapper">
      <form className="form">
        <h1 className="title">Login</h1>
        {errorMessage && <div className="error-message">{errorMessage}</div>}
        <div className="input-group">
          <label className="label">Roll Number:</label>
          <input className="input" type="text" value={rollNumber} onChange={handleRollNumberChange} />
        </div>
        <div className="input-group">
          <label className="label">Student Name:</label>
          <input className="input" type="text" value={studentName} onChange={handleStudentNameChange} />
        </div>
        <button className="button" type="button" onClick={handleLogin}>
          Attempt Quiz !
        </button>
      </form>
    </div>
  );
}

export default StudentLogin;
