import React, { useState } from "react";
import _ from "lodash";
import "./Quiz.css";
import Score from "./Score";

const Quiz = ({ quizData }) => {
  const [questions, setQuestions] = useState(_.shuffle(quizData));
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);

  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  const playAgain = () => {
    setQuestions(_.shuffle(quizData));
    setCurrentQuestion(0);
    setShowScore(false);
    setScore(0);
  };

  return (
    <div className="quiz-container">
      {showScore ? (
        <Score score={score} totalQuestions={questions.length} playAgain={playAgain} />
      ) : (
        <div className="quiz">
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{questions.length}
            </div>
            <div className="question-text">{questions[currentQuestion].question}</div>
          </div>
          <div className="answer-section">
            {_.chunk(_.shuffle(questions[currentQuestion].options), 2).map((optionChunk, index) => (
              <div className="answer-row" key={index}>
                {optionChunk.map((option, index) => (
                  <button className="answer-button" key={index} onClick={() => handleAnswerOptionClick(option === questions[currentQuestion].answer)}>
                    {option}
                  </button>
                ))}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Quiz;
