import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './TeacherLogin.css';

function TeacherLogin() {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleConfirmPasswordChange = (event) => {
    setConfirmPassword(event.target.value);
  };

  const handleLogin = () => {
    // TODO: Implement login logic
  };

  const handleSignup = () => {
    // TODO: Implement signup logic
  };

  const toggleForm = () => {
    setIsLogin(!isLogin);
  };

  return (
    <div className="wrapper">
      <h1 className="title">{isLogin ? 'Teacher Login' : 'Teacher Signup'}</h1>
      <form className="form">
        <div className="input-group">
          <label className="label">Username:</label>
          <input className="input" type="text" value={username} onChange={handleUsernameChange} />
        </div>
        <div className="input-group">
          <label className="label">Password:</label>
          <input className="input" type="password" value={password} onChange={handlePasswordChange} />
        </div>
        {!isLogin && (
          <div className="input-group">
            <label className="label">Confirm Password:</label>
            <input className="input" type="password" value={confirmPassword} onChange={handleConfirmPasswordChange} />
          </div>
        )}
        {isLogin ? (
          <button className="button" type="button" onClick={handleLogin}>
            Login
          </button>
        ) : (
          <button className="button" type="button" onClick={handleSignup}>
            Signup
          </button>
        )}
        {isLogin ? (
          <p className="message">
            Don't have an account? <Link to="#" onClick={toggleForm}>Signup</Link>
          </p>
        ) : (
          <p className="message">
            Already have an account? <Link to="#" onClick={toggleForm}>Login</Link>
          </p>
        )}
      </form>
    </div>
  );
}

export default TeacherLogin;
