import React from 'react';
import correctImage from '../images/a1.gif';
import incorrectImage from '../images/incorrect.GIF';

const AnswerStatus = ({ answerStatus }) => {
  if (!answerStatus) {
    return null;
  }

  return (
    <div className="answer-status">
      <img src={answerStatus === 'correct' ? correctImage : incorrectImage} alt={answerStatus} />
    </div>
  );
};

export default AnswerStatus;
