import React, { useState } from 'react';
import { Route, Switch, useLocation } from 'react-router-dom';
import StartScreen from './components/StartScreen';
import TeacherLogin from './components/TeacherLogin';
import StudentLogin from './components/StudentLogin';
import Quiz from './components/Quiz';
import Score from './components/Score';
import Practice from './components/Practice';
import Navbar from './components/Navbar';
import './App.css';

function App() {
  const location = useLocation();
  const hideNavbarRoutes = ['/quiz', '/practice'];
  const [loggedIn, setLoggedIn] = useState(false);

  return (
    <div className="App">
      {hideNavbarRoutes.indexOf(location.pathname) === -1 && <Navbar />}
      <Switch>
        <Route exact path="/">
          <div className="start-screen-background">
            <StartScreen />
          </div>
        </Route>
        <Route exact path="/teacher-login">
          <div className="login-background">
            <TeacherLogin setLoggedIn={setLoggedIn} />
          </div>
        </Route>
        {loggedIn && (
          <>
            <Route exact path="/quiz">
              <div className="quiz-background">
                <Quiz />
              </div>
            </Route>
            <Route exact path="/practice">
              <div className="practice-background">
                <Practice />
              </div>
            </Route>
          </>
        )}
        <Route path="/student-login">
          <div className="login-background">
            <StudentLogin />
          </div>
        </Route>
        <Route exact path="/score">
          <div className="score-background">
            <Score />
          </div>
        </Route>
      </Switch>
    </div>
  );
}

export default App;
