import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './TeacherLogin.css';

function TeacherLogin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isSignup, setIsSignup] = useState(false);
  const [loggedIn, setLoggedIn] = useState(false);
  const [loginError, setLoginError] = useState(false);
  const [signupError, setSignupError] = useState(false);

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleConfirmPasswordChange = (event) => {
    setConfirmPassword(event.target.value);
  };

  const handleSignup = (event) => {
    event.preventDefault();
    if (password !== confirmPassword) {
      setSignupError(true);
      return;
    }
    // TODO: Implement teacher signup logic using username and password
    setLoggedIn(true);
  };

  const handleLogin = (event) => {
    event.preventDefault();
    // TODO: Implement teacher login logic using username and password
    if (username === 'teacher' && password === 'password') {
      setLoggedIn(true);
      setLoginError(false);
    } else {
      setLoggedIn(false);
      setLoginError(true);
    }
  };

  const toggleSignup = () => {
    setIsSignup(!isSignup);
    setLoginError(false);
    setSignupError(false);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUsername('');
    setPassword('');
    setConfirmPassword('');
  };

  return (
    <div className="TeacherLogin">
      {!loggedIn ? (
        <div>
          <h1>{isSignup ? 'Teacher Signup' : 'Teacher Login'}</h1>
          <form onSubmit={isSignup ? handleSignup : handleLogin}>
            <label>
              Username:
              <input type="text" value={username} onChange={handleUsernameChange} />
            </label>
            <br />
            <label>
              Password:
              <input type="password" value={password} onChange={handlePasswordChange} />
            </label>
            <br />
            {isSignup && (
              <div>
                <label>
                  Confirm Password:
                  <input type="password" value={confirmPassword} onChange={handleConfirmPasswordChange} />
                </label>
                <br />
                {signupError && <div className="error-message">Passwords do not match</div>}
              </div>
            )}
            <button type="submit">{isSignup ? 'Sign up' : 'Login'}</button>
          </form>
          {!isSignup && (
            <p>
              Don't have an account? <button onClick={toggleSignup}>Sign up</button>
            </p>
          )}
          {loginError && <div className="error-message">Invalid username or password</div>}
        </div>
      ) : (
        <div>
          <h1>Welcome, {username}!</h1>
          <button onClick={handleLogout}>Logout</button>
        </div>
      )}
    </div>
  );
}

export default TeacherLogin;
