import React, { useState, useEffect } from 'react';
import { VictoryChart, VictoryBar, VictoryAxis, VictoryTooltip } from 'victory';
import axios from 'axios';
import './styles/ScoreDistribution.css';

const ScoreDistribution = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8000/api/result')
      .then(response => {
        const histogramData = calculateHistogramData(response.data);
        setData(histogramData);
        setIsLoading(false);
      })
      .catch(error => {
        console.log(error);
        setIsLoading(false);
      });
  }, []);

  const calculateHistogramData = (students) => {
    const topics = new Set(students.map(student => student.topic));
    const bins = [0, 5, 10, 15, 20, 25];
    const histogramData = [];

    for (const topic of topics) {
      const topicStudents = students.filter(student => student.topic === topic);
      const scores = topicStudents.map(student => student.point);
      const binCounts = new Array(bins.length - 1).fill(0);

      for (const score of scores) {
        for (let i = 1; i < bins.length; i++) {
          if (score >= bins[i-1] && score < bins[i]) {
            binCounts[i-1]++;
            break;
          }
        }
      }

      const topicHistogramData = binCounts.map((count, i) => ({
        x: `${bins[i]}-${bins[i+1]}`,
        y: count,
        topic: topic
      }));

      histogramData.push(...topicHistogramData);
    }

    return histogramData;
  };

  const getUniqueRollNumbers = (data) => {
    const rollNumbers = new Set();
    data.forEach((student) => {
      rollNumbers.add(student.roll_no);
    });
    return rollNumbers.size;
  };

  const getMaxYValue = (data) => {
    const rollNumbersCount = getUniqueRollNumbers(data);
    const maxYValue = Math.ceil(rollNumbersCount / 10) * 10;
    const tickCount = maxYValue / 5;
    return { maxYValue, tickCount };
  };

  const { maxYValue, tickCount } = getMaxYValue(data);

  if (isLoading) {
    return (
      <div className="loading">
        <p>Loading</p>
      </div>
    );
  }

  return (
    <div className="score-distribution">
      <h2>Topic Wise Score Distribution</h2>
      <VictoryChart
        domainPadding={20}
      >
        <VictoryAxis
          dependentAxis
          label="No. of Students"
          tickValues={[...Array(maxYValue / 5 + 1).keys()].map((i) => i * 5)}
          tickFormat={(t) => `${t}`}
        />
        <VictoryAxis
          label="Score"
          tickValues={data.filter((d, i, arr) => arr.findIndex(t => t.x === d.x) === i).map(bin => bin.x)}
          style={{
            axis: { stroke: '#000' },
            tickLabels: { fontSize: 12, padding: 5 },
            }}
/>
{data.filter((d, i, arr) => arr.findIndex(t => t.x === d.x) === i).map((bin) => (
<VictoryBar
key={bin.x}
data={data.filter(d => d.x === bin.x)}
x="topic"
y="y"
style={{
data: { fill: "#c43a31" },
labels: { fontSize: 12, fontWeight: 'bold' },
}}
labelComponent={<VictoryTooltip />}
/>
))}
</VictoryChart>
</div>
);
};

const calculateHistogramData = (scores) => {
const topics = ['Maths', 'Science', 'History', 'Geography', 'English'];
const bins = [0, 5, 10, 15, 20, 25];
const binCounts = new Array(topics.length * (bins.length - 1)).fill(0);
for (const score of scores) {
const { topic, point } = score;
const topicIndex = topics.indexOf(topic);
for (let i = 1; i < bins.length; i++) {
if (point >= bins[i-1] && point < bins[i]) {
binCounts[topicIndex * (bins.length - 1) + (i-1)]++;
break;
}
}
}
const histogramData = binCounts.map((count, i) => ({
topic: topics[Math.floor(i / (bins.length - 1))],
x: `${bins[i % (bins.length - 1)]}-${bins[i % (bins.length - 1) + 1]}`,

y: count,
}));
return histogramData;
};

export default ScoreDistribution;
