import React, { useState } from 'react';

const EditQuestionForm = ({ question, onUpdateQuestion, onCancel }) => {
  const [updatedQuestion, setUpdatedQuestion] = useState({
    ...question,
    options: [...question.options],
  });

  const handleOptionChange = (index, value) => {
    setUpdatedQuestion((prevQuestion) => {
      const updatedOptions = [...prevQuestion.options];
      updatedOptions[index] = value;
      return { ...prevQuestion, options: updatedOptions };
    });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUpdatedQuestion((prevQuestion) => ({ ...prevQuestion, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onUpdateQuestion(updatedQuestion);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Question:
        <input type="text" name="question" value={updatedQuestion.question} onChange={handleInputChange} />
      </label>

      <label>
        Option A:
        <input
          type="text"
          name="optionA"
          value={updatedQuestion.options[0]}
          onChange={(event) => handleOptionChange(0, event.target.value)}
        />
      </label>

      <label>
        Option B:
        <input
          type="text"
          name="optionB"
          value={updatedQuestion.options[1]}
          onChange={(event) => handleOptionChange(1, event.target.value)}
        />
      </label>

      <label>
        Option C:
        <input
          type="text"
          name="optionC"
          value={updatedQuestion.options[2]}
          onChange={(event) => handleOptionChange(2, event.target.value)}
        />
      </label>

      <label>
        Option D:
        <input
          type="text"
          name="optionD"
          value={updatedQuestion.options[3]}
          onChange={(event) => handleOptionChange(3, event.target.value)}
        />
      </label>

      <label>
        Answer:
        <select name="answer" value={updatedQuestion.answer} onChange={handleInputChange}>
          {updatedQuestion.options.map((option, index) => (
            <option key={option} value={index}>
              {option}
            </option>
          ))}
        </select>
      </label>

      <label>
        Difficulty:
        <select name="difficulty" value={updatedQuestion.difficulty} onChange={handleInputChange}>
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>
      </label>

      <div>
        <button type="submit">Save</button>
        <button type="button" onClick={onCancel}>
          Cancel
        </button>
      </div>
    </form>
  );
};

export default EditQuestionForm;
