import React from 'react';
import { Route, Switch, useLocation } from 'react-router-dom';
import StartScreen from './components/StartScreen';
import TeacherLogin from './components/TeacherLogin';
import StudentLogin from './components/StudentLogin';
import Quiz from './components/Quiz';
import Score from './components/Score';
import Practice from './components/Practice';
import Navbar from './components/Navbar';
import Teacher from './components/Teacher';
import './App.css';

function App() {
  const location = useLocation();
  const hideNavbarRoutes = ['/quiz', '/practice' ,'/Quiz' , '/Practice'];

  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('isLoggedIn') === 'true');

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('isLoggedIn');
    history.push('/');
  }

  return (
    <div className="App">
      {hideNavbarRoutes.indexOf(location.pathname) === -1 && <Navbar isLoggedIn={isLoggedIn} handleLogout={handleLogout} />}
      <Switch>
        <Route exact path="/">
          <div className="start-screen-background">
            <StartScreen />
          </div>
        </Route>
        <Route exact path="/teacher-login">
          <div className="login-background">
            <TeacherLogin />
          </div>
        </Route>
        <Route exact path="/student-login">
          <div className="login-background">
            <StudentLogin />
          </div>
        </Route>
        <Route exact path="/quiz">
          <div className="quiz-background">
            <Quiz />
          </div>
        </Route>
        <Route exact path="/practice">
          <div className="practice-background">
            <Practice />
          </div>
        </Route>
        <Route exact path="/teacher">
          <div className="teacher-background">
            <Teacher />
          </div>
        </Route>
      </Switch>
    </div>
  );
}

export default App;
