const generatePdf = () => {
  const input = document.querySelector('#pdf-content');

  html2canvas(input).then((canvas) => {
    const imgData = canvas.toDataURL('image/png');
    const pdf = new jsPDF();
    const imgProps = pdf.getImageProperties(imgData);
    const pdfWidth = pdf.internal.pageSize.getWidth() - 20;
    const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

    // Define A4 dimensions
    const A4_WIDTH = 210;
    const A4_HEIGHT = 297;

    // Split the PDF into multiple pages, one for each component
    const components = input.children;
    for (let i = 0; i < components.length; i++) {
      const component = components[i];
      html2canvas(component).then((componentCanvas) => {
        const componentImgData = componentCanvas.toDataURL('image/png');
        const componentImgProps = pdf.getImageProperties(componentImgData);
        const componentPdfHeight = (componentImgProps.height * pdfWidth) / componentImgProps.width;

        // Calculate center position
        const componentPdfWidth = pdfWidth;
        const centerX = A4_WIDTH / 2 - componentPdfWidth / 2;
        const centerY = A4_HEIGHT / 2 - componentPdfHeight / 2;

        // Add component image to PDF at center position
        pdf.addImage(componentImgData, 'PNG', centerX, centerY, componentPdfWidth, componentPdfHeight);

        let contentHeight = componentPdfHeight + 10;
        const pageHeight = pdf.internal.pageSize.getHeight();

        while (contentHeight > pageHeight) {
          pdf.addPage();
          const position = 10 - pageHeight;
          pdf.addImage(componentImgData, 'PNG', 10, position, pdfWidth, componentPdfHeight);
          contentHeight -= pageHeight;
        }

        if (i < components.length - 1) {
          pdf.addPage();
        } else {
          pdf.save('dashboard.pdf');
        }
      });
    }
  });
};


const Dashboard = () => {
  return (
    <div className="dashboard">

      <div id="pdf-content">
      <h1>Analysis Report</h1>
        <ScoreDistribution />
        <PassFailRate />
        <TopicwiseTopPerformers />
      </div>
      <div className="button-container">
        <Link to="/scoreboard"><button>Back</button></Link>
        <button onClick={generatePdf}>Download PDF</button>
        <Link to="/"><button>Logout</button></Link>
      </div>
    </div>
  );
};

export default Dashboard;
