import React from 'react';
import { Link } from 'react-router-dom';
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
import ScoreDistribution from './ScoreDistribution';
import PassFailRate from './PassFailRate';
import TopicwiseTopPerformers from './TopicwiseTopPerformers';
import './styles/Dashboard.css';

pdfMake.vfs = pdfFonts.pdfMake.vfs;

const generatePdf = () => {
  const docDefinition = {
    content: [
      { text: 'Analysis Report', style: 'header' },
      '\n',
      { text: 'Topicwise Top Performers', style: 'subheader' },
      { canvas: [{ type: 'line', x1: 0, y1: 5, x2: 595, y2: 5, lineWidth: 1 }] },
      { table: { body: TopicwiseTopPerformers.getTableData() } },
      { text: 'Score Distribution', style: 'subheader' },
      { canvas: [{ type: 'line', x1: 0, y1: 5, x2: 595, y2: 5, lineWidth: 1 }] },
      { table: { body: ScoreDistribution.getTableData() } },
      { text: 'Pass-Fail Rate', style: 'subheader' },
      { canvas: [{ type: 'line', x1: 0, y1: 5, x2: 595, y2: 5, lineWidth: 1 }] },
      { table: { body: PassFailRate.getTableData() } },
    ],
    styles: {
      header: { fontSize: 18, bold: true, alignment: 'center', margin: [0, 0, 0, 10] },
      subheader: { fontSize: 14, bold: true, margin: [0, 10, 0, 5] },
      tableHeader: { bold: true, fontSize: 12, color: 'black' },
    },
  };

  pdfMake.createPdf(docDefinition).download('dashboard.pdf');
};

const Dashboard = () => {
  return (
    <div className="dashboard">
      <div className="pdf-content">
        <h1>Analysis Report</h1>
        <TopicwiseTopPerformers />
        <ScoreDistribution />
        <PassFailRate />
      </div>
      <div className="button-container">
        <Link to="/scoreboard">
          <button>Back</button>
        </Link>
        <button onClick={generatePdf}>Download PDF</button>
        <Link to="/">
          <button>Logout</button>
        </Link>
      </div>
    </div>
  );
};

export default Dashboard;
