import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { LineChart, Line, BarChart, Bar, DoughnutChart, Pie } from 'recharts';

const Dashboard = () => {
  const [students, setStudents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    axios
      .get('http://localhost:8000/api/result')
      .then(response => {
        const sortedStudents = response.data.sort((a, b) => new Date(a.date) - new Date(b.date));
        setStudents(sortedStudents);
        setIsLoading(false);
      })
      .catch(error => {
        console.log(error);
        setIsLoading(false);
      });
  }, []);

  if (isLoading) {
    return (
      <div>
        <p>Loading...</p>
      </div>
    );
  }

  const data = students.map(student => {
    return {
      name: student.name,
      score: student.score,
      date: new Date(student.date).toLocaleDateString(),
    };
  });

  return (
    <div>
      <h1>Dashboard</h1>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <LineChart width={500} height={300} data={data}>
          <Line type="monotone" dataKey="score" stroke="#8884d8" />
        </LineChart>
        <BarChart width={500} height={300} data={data}>
          <Bar dataKey="score" fill="#8884d8" />
        </BarChart>
        <DoughnutChart width={500} height={300} data={data}>
          <Pie dataKey="score" fill="#8884d8" />
        </DoughnutChart>
      </div>
    </div>
  );
};

export default Dashboard;
