import React, { useState, useEffect } from 'react';
import './styles/Scoreboard.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Scoreboard = () => {
  const [students, setStudents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [topic, setTopic] = useState('');

  useEffect(() => {
    axios.get('http://localhost:8000/api/result')
      .then(response => {
        const sortedStudents = response.data.sort((a, b) => b.point - a.point);
        setStudents(sortedStudents);
        setTopic(response.data[0].topic); // assuming the topic is stored in the first result
        setIsLoading(false);
      })
      .catch(error => {
        console.log(error);
        setIsLoading(false);
      });
  }, []);

  const handleDelete = () => {
    axios.delete('http://localhost:8000/api/result')
      .then(response => {
        window.location.reload();
      })
      .catch(error => {
        console.log(error);
      });
  }

  if (isLoading) {
    return (
      <div className="loading">
        <p>Loading</p>
      </div>
    );
  }

  if (students.length === 0) {
    return (
      <div className="scoreboard-empty">
        <h1>SCORE BOARD</h1>
        <p>Scoreboard is empty!!</p>
        <div className="scoreboard-buttons">
          <Link to="/Teacher"><button>Home</button></Link>
          <Link to="/"><button>Logout</button></Link>
        </div>
      </div>
    );
  }

  return (
    <div className="scoreboard">
      <h1>SCORE BOARD - {topic}</h1> // add topic here
      <table>
        <thead>
          <tr>
            <th>S.No.</th>
            <th>Student Name</th>
            <th>Roll Number</th>
            <th>Score</th>
            <th>Result</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student, index) => (
            <tr key={student._id}>
              <td>{index + 1}</td>
              <td>{student.studentName}</td>
              <td>{student.rollNumber}</td>
              <td>{student.point}</td>
              <td>{student.point >= 4 ? (<td className="result-pass">Pass</td>) : (<td className="result-fail">Fail</td>)}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="scoreboard-buttons">
        <Link to="/Teacher"><button>Home</button></Link>
        <button onClick={handleDelete}>Delete Results</button>
        <Link to="/"><button>Logout</button></Link>
      </div>
    </div>
  );
};

export default Scoreboard;
