import React, { useState, useEffect, useRef } from 'react';
import _ from 'lodash';
import { Link } from 'react-router-dom';
import './styles/Quiz.css';
import Score from './Score';
import axios from 'axios';

const Quiz = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [hasPostedResults, setHasPostedResults] = useState(false);
  const [previousDifficulty, setPreviousDifficulty] = useState(null);

  // Define variables here
  let easyQuestions = [];
  let mediumQuestions = [];
  let hardQuestions = [];

useEffect(() => {
  // Make an HTTP GET request to your backend API endpoint
  axios
    .get('http://localhost:8000/api/questions')
    .then((response) => {
      // Shuffle the questions for each difficulty level
      let easyQuestions = _.shuffle(response.data.filter((question) => question.difficulty === 'easy'));
      let mediumQuestions = _.shuffle(response.data.filter((question) => question.difficulty === 'medium'));
      let hardQuestions = _.shuffle(response.data.filter((question) => question.difficulty === 'hard'));

      // Combine the shuffled questions into a single array
      let allQuestions = [...easyQuestions, ...mediumQuestions, ...hardQuestions];

      // Set the questions state variable to all the shuffled questions
      setQuestions(allQuestions);

      // Set the current difficulty to 'easy'
      let currentDifficulty = 'easy';

      // Set the number of answered questions to 0
      let answeredQuestions = 0;

      // Set the score to 0
      setScore(0);

      // Set the current question index to 0
      setCurrentQuestion(0);

      setIsLoading(false);
    })
    .catch((error) => {
      console.log(error);
      setIsLoading(false);
    });
}, []);


  const handleAnswerOptionClick = (isCorrect, difficulty) => {
    let questionScore = 0;
    if (isCorrect) {
      if (difficulty === 'easy') {
        questionScore = 1;
      } else if (difficulty === 'medium') {
        questionScore = 2;
      } else if (difficulty === 'hard') {
        questionScore = 3;
      }
      setScore(score + questionScore);
    }

    const nextQuestion = currentQuestion + 1;

    if (nextQuestion === 2 && score === 2) {
      // If the first two questions were easy and answered correctly,
      // select a medium question as the next question
      const selectedMediumQuestion = _.sample(mediumQuestions);
      setQuestions([...questions, selectedMediumQuestion]);
    } else if (nextQuestion === 3 && score < 3) {
      // If the third question is hard and the score is less than 3,
      // select an easy question as the next question
      const selectedEasyQuestion = _.sample(easyQuestions);
      setQuestions([...questions, selectedEasyQuestion]);
    } else if (nextQuestion === 3 && score >= 3) {
      // If the third question is hard and the score is greater than or equal to 3,
      // select a hard question as the next question
      const selectedHardQuestion = _.sample(hardQuestions);
      setQuestions([...questions, selectedHardQuestion]);
    } else if (nextQuestion === 4 && score < 5) {
      // If the fourth question is medium and the score is less than 5,
      // select an easy question as the next question
    const selectedEasyQuestion = _.sample(easyQuestions);
    setQuestions([...questions, selectedEasyQuestion]);
    } else if (nextQuestion === 4 && score >= 5) {
    // If the fourth question is medium and the score is greater than or equal to 5,
    // select a hard question as the next question
    const selectedHardQuestion = _.sample(hardQuestions);
    setQuestions([...questions, selectedHardQuestion]);
    } else if (nextQuestion === 5 && score < 7) {
    // If the fifth question is hard and the score is less than 7,
    // select a medium question as the next question
    const selectedMediumQuestion = _.sample(mediumQuestions);
    setQuestions([...questions, selectedMediumQuestion]);
    } else if (nextQuestion === 5 && score >= 7) {
    // If the fifth question is hard and the score is greater than or equal to 7,
    // select an easy question as the next question
    const selectedEasyQuestion = _.sample(easyQuestions);
    setQuestions([...questions, selectedEasyQuestion]);
    } else {
    setShowScore(true);
    }
    setCurrentQuestion(nextQuestion);
  };



useEffect(() => {
  console.log('Current Score:', score);
}, [score]);

  return (
    <div className="quiz">
      {isLoading ? (
        <div className="loading">Loading</div>
      ) : showScore ? (
        <Score
          score={score}
          totalQuestions={questions.length}
          hasPostedResults={hasPostedResults}
          setHasPostedResults={setHasPostedResults}
        />
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{questions.length}
            </div>
            <div className="question-text">{questions[currentQuestion].question}</div>
            <div className="difficulty-level">
              Difficulty level: {questions[currentQuestion].difficulty}
            </div>
          </div>
          <div className="answer-section">
            {_.chunk(_.shuffle(questions[currentQuestion].options), 2).map((optionChunk) => (
              <div className="answer-row">
                {optionChunk.map((option) => (
                  <button
                    onClick={() =>
                      handleAnswerOptionClick(
                        option === questions[currentQuestion].answer,
                        questions[currentQuestion].difficulty
                      )
                    }
                  >
                    {option}
                  </button>
                ))}
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default Quiz;
