import React, { useState } from 'react';
import _ from 'lodash';
import { Link } from 'react-router-dom'
import './Practice.css';

import correctImage from './a1.gif';
import incorrectImage from './a1.gif';
import {practiceData} from './practiceData';



const Practice = () => {
  const [questions, setQuestions] = useState(_.shuffle(practiceData));
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);
  const [answerStatus, setAnswerStatus] = useState(null);



  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setAnswerStatus('correct');
      setScore(score + 1);
    } else {
      setAnswerStatus('incorrect');
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setTimeout(() => {
        setCurrentQuestion(nextQuestion);
        setAnswerStatus(null);
      }, 1000); // show answer status for 1 second before moving to next question
    } else {
      setShowScore(true);
    }
  };

  const restartQuiz = () => {
    setQuestions(_.shuffle(practiceData));
    setCurrentQuestion(0);
    setShowScore(false);
    setScore(0);
    setAnswerStatus(null);
  };

  return (
    <div className="practice">
      {showScore ? (
        <>
          <div className="score-section">
            <h3>You scored {score} out of {questions.length}</h3>
            <button className="btn btn-primary" onClick={restartQuiz}>Restart</button>
          </div>
        </>
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{questions.length}
            </div>
            <div className="question-text">{questions[currentQuestion].question}</div>
            {answerStatus && (
              <div className="answer-status">
                <img src={answerStatus === 'correct' ? correctImage : incorrectImage} alt={answerStatus} />
              </div>
            )}
          </div>
          <div className="answer-section">
            {_.chunk(_.shuffle(questions[currentQuestion].options), 2).map((optionChunk, i) => (
              <div className="answer-row" key={i}>
                {optionChunk.map((option) => (
                  <button className="btn btn-outline-primary" disabled={answerStatus} onClick={() => handleAnswerOptionClick(option === questions[currentQuestion].answer)}>
                    {option}
                  </button>
                ))}
              </div>
            ))}
          </div>
          <div className="navigation-section">
            <Link className="btn btn-primary" to="/">Home</Link>
            <button className="btn btn-primary" onClick={() => setCurrentQuestion(currentQuestion + 1)} disabled={currentQuestion === questions.length - 1}>{currentQuestion === questions.length - 1 ? 'Finish' : 'Next'}</button>
          </div>
        </>
      )}
    </div>
  );
};

export default Practice;
