import React, { useState } from 'react';
import axios from 'axios';
import './styles/AddQuestionForm.css';
import { Link } from 'react-router-dom';


const AddQuestionForm = ({ onAddQuestion, quizId, history }) => {
  const [question, setQuestion] = useState('');
  const [options, setOptions] = useState(['', '', '', '']);
  const [answer, setAnswer] = useState('');
  const [difficulty, setDifficulty] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    const newQuestion = {
      question,
      options,
      answer,
      difficulty,
    };
    fetch(`api/questions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ ...newQuestion, quizId }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Success:', data);
        onAddQuestion(data);
        setQuestion('');
        setOptions(['', '', '', '']);
        setAnswer('');
        setDifficulty('');
        history.push(`/edit-quiz/${quizId}`);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...options];
    newOptions[index] = value;
    setOptions(newOptions);
  };

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <div className="form-control">
        <label>Question</label>
        <input
          type="text"
          placeholder="Enter question"
          value={question}
          onChange={(event) => setQuestion(event.target.value)}
        />
      </div>
      <div className="form-control">
        <label>Option A</label>
        <input
          type="text"
          placeholder="Enter option A"
          value={options[0]}
          onChange={(event) => handleOptionChange(0, event.target.value)}
        />
      </div>
      <div className="form-control">
        <label>Option B</label>
        <input
          type="text"
          placeholder="Enter option B"
          value={options[1]}
          onChange={(event) => handleOptionChange(1, event.target.value)}
        />
      </div>
      <div className="form-control">
        <label>Option C</label>
        <input
          type="text"
          placeholder="Enter option C"
          value={options[2]}
          onChange={(event) => handleOptionChange(2, event.target.value)}
        />
      </div>
      <div className="form-control">
        <label>Option D</label>
        <input
          type="text"
          placeholder="Enter option D"
          value={options[3]}
          onChange={(event) => handleOptionChange(3, event.target.value)}
        />
      </div>
      <div className="form-control">
        <label>Answer</label>
        <select value={answer} onChange={(event) => setAnswer(event.target.value)}>
          <option value="">Select answer</option>
          <option value="A">A</option>
          <option value="B">B</option>
          <option value="C">C</option>
          <option value="D">D</option>
        </select>
      </div>

<div className="form-control">
        <label>Difficulty</label>
        <select value={difficulty} onChange={(event) => setDifficulty(event.target.value)}>
          <option value="">Select difficulty</option>
<option value="easy">Easy</option>
<option value="medium">Medium</option>
<option value="hard">Hard</option>
</select>
</div>
<div className="form-control">
<button type="submit" className="btn btn-block">Add Question</button>
<button type="button" className="btn btn-block" onClick={() => history.goBack()}>Cancel</button>
</div>
</form>
);
};

export default AddQuestionForm;
