import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import './TeacherLogin.css';

function TeacherLogin() {
  const history = useHistory();

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = () => {
    if (username === 'admin' && password === 'admin123') {
      // Redirect to the teacher page if the login is successful
      history.push('/teacher');
    } else {
      // Set an error message if the login is unsuccessful
      setErrorMessage('Invalid username or password');
    }
  };

  return (
    <div className="wrapper">
      <form className="form">
        <h1 className="title">Login</h1>
        <div className="input-group">
          <label className="label">Username:</label>
          <input className="input" type="text" value={username} onChange={handleUsernameChange} />
        </div>
        <div className="input-group">
          <label className="label">Password:</label>
          <input className="input" type="password" value={password} onChange={handlePasswordChange} />
        </div>
        {errorMessage && <p className="error">{errorMessage}</p>}
        <button className="button" type="button" onClick={handleLogin}>
          Login
        </button>
      </form>
    </div>
  );
}

export default TeacherLogin;
