import React from 'react';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

import { Link } from 'react-router-dom';
import ScoreDistribution from './ScoreDistribution';
import PassFailRate from './PassFailRate';
import TopicwiseTopPerformers from './TopicwiseTopPerformers';
import './styles/Dashboard.css';

/*const generatePdf = () => {
  const input = document.querySelector('#pdf-content');

  html2canvas(input).then((canvas) => {
    const imgData = canvas.toDataURL('image/png');
    const pdf = new jsPDF();
    const imgProps = pdf.getImageProperties(imgData);
    const pdfWidth = pdf.internal.pageSize.getWidth() - 20;
    const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

    pdf.addImage(imgData, 'PNG', 10, 10, pdfWidth, pdfHeight);
    pdf.save('dashboard.pdf');
  });
};
*/
const generatePdf = () => {
  const pdf = new jsPDF();
  const input1 = document.querySelector('#pdf-content1');
  const input2 = document.querySelector('#pdf-content2');
  const input3 = document.querySelector('#pdf-content3');

  html2canvas(input1).then((canvas1) => {
    const imgData1 = canvas1.toDataURL('image/png');
    const imgProps1 = pdf.getImageProperties(imgData1);
    const pdfWidth = pdf.internal.pageSize.getWidth() - 20;
    const pdfHeight = (imgProps1.height * pdfWidth) / imgProps1.width;

    pdf.addImage(imgData1, 'PNG', 10, 10, pdfWidth, pdfHeight);
    pdf.addPage();

    html2canvas(input2).then((canvas2) => {
      const imgData2 = canvas2.toDataURL('image/png');

      pdf.addImage(imgData2, 'PNG', 10, 10, pdfWidth, pdfHeight);
      pdf.addPage();

      html2canvas(input3).then((canvas3) => {
        const imgData3 = canvas3.toDataURL('image/png');
        let contentHeight = pdfHeight + 10;
        const pageHeight = pdf.internal.pageSize.getHeight();

        pdf.addImage(imgData3, 'PNG', 10, 10, pdfWidth, pdfHeight);
        contentHeight += pdfHeight + 10;

        while (contentHeight > pageHeight) {
          pdf.addPage();
          const position = 10 - pageHeight;
          pdf.addImage(imgData3, 'PNG', 10, position, pdfWidth, pdfHeight);
          contentHeight -= pageHeight;
        }

        pdf.save('dashboard.pdf');
      });
    });
  });
};


const Dashboard = () => {
  return (
    <div className="dashboard">

      <div id="pdf-content">
      <h1>Analysis Report</h1>
        <ScoreDistribution />
      </div>
      <div id="pdf-content">

        <PassFailRate />
      </div>
      <div id="pdf-content">

        <TopicwiseTopPerformers />
      </div>
      <div className="button-container">
        <Link to="/scoreboard"><button>Back</button></Link>
        <button onClick={generatePdf}>Download PDF</button>
        <Link to="/"><button>Logout</button></Link>
      </div>
    </div>
  );
};

export default Dashboard;
