import React, { useState, useEffect } from 'react';
import { VictoryChart, VictoryBar, VictoryAxis, VictoryTooltip } from 'victory';
import axios from 'axios';
import './styles/ScoreDistribution.css';

const ScoreDistribution = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [data, setData] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState("");

  useEffect(() => {
    axios.get('http://localhost:8000/api/result')
      .then(response => {
        const scoresByTopic = groupScoresByTopic(response.data);
        const histogramData = calculateHistogramData(scoresByTopic);
        setData(histogramData);
        setIsLoading(false);
        setSelectedTopic(Object.keys(scoresByTopic)[0]);
      })
      .catch(error => {
        console.log(error);
        setIsLoading(false);
      });
  }, []);

  const groupScoresByTopic = (data) => {
    const scoresByTopic = {};
    for (const student of data) {
      const { topic, point } = student;
      if (!scoresByTopic[topic]) {
        scoresByTopic[topic] = [];
      }
      scoresByTopic[topic].push(point);
    }
    return scoresByTopic;
  };

  const topicColorMap = {
    "Topic 1": "#c43a31",
    "Topic 2": "#ff7f0e",
    "Topic 3": "#2ca02c",
    "Topic 4": "#9467bd",
    // add more topics and colors here...
  };

  const calculateHistogramData = (scoresByTopic) => {
    const bins = [0, 5, 10, 15, 20, 25];
    const histogramData = [];
    const scores = scoresByTopic[selectedTopic];
    const binCounts = new Array(bins.length - 1).fill(0);
    for (const score of scores) {
      for (let i = 1; i < bins.length; i++) {
        if (score >= bins[i-1] && score < bins[i]) {
          binCounts[i-1]++;
          break;
        }
      }
    }
    const topicHistogramData = binCounts.map((count, i) => ({ x: `${bins[i]}-${bins[i+1]}`, y: count, topic: selectedTopic }));
    histogramData.push(...topicHistogramData);
    return histogramData;
  };

  const handleTopicChange = (event) => {
    setSelectedTopic(event.target.value);
  };

  const getUniqueRollNumbers = (data) => {
    return new Set(data.map(student => student.roll_no)).size;
  };

  const getMaxYValue = (data) => {
    const rollNumbersCount = getUniqueRollNumbers(data);
    const maxYValue = Math.ceil(rollNumbersCount / 10) * 10;
    const tickCount = maxYValue / 5;
    return { maxYValue, tickCount };
  };

  const { maxYValue, tickCount } = getMaxYValue(data);

  if (isLoading) {
    return (
      <div className="loading">
        <p>Loading</p>
      </div>
    );
  }

return (
  <div className="score-distribution">
    <h2>Topic Wise Score Distribution</h2>
    <label htmlFor="topics">Select Topic:</label>
    <select id="topics" name="topics" value={selectedTopic} onChange={handleTopicChange}>
      {Object.keys(groupScoresByTopic(data)).map(topic => (
        <option key={topic} value={topic}>{topic}</option>
      ))}
    </select>
    <VictoryChart domainPadding={20} height={300} width={500}>
      <VictoryAxis />
      <VictoryAxis dependentAxis tickCount={tickCount} domain={[0, maxYValue]} />
      <VictoryBar
        data={data}
        x="x"
        y="y"
        style={{
          data: {
            fill: topicColorMap[selectedTopic]
          },
          labels: {
            fontSize: 12,
            fill: "#333"
          }
        }}
        labelComponent={<VictoryTooltip />}
      />
    </VictoryChart>
  </div>
);
};

export default ScoreDistribution;
