import React, { useState, useEffect } from 'react';
import _ from 'lodash';
import { Link } from 'react-router-dom';
import './styles/Quiz.css';
import Score from './Score';
import axios from 'axios';

const Quiz = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [hasPostedResults, setHasPostedResults] = useState(false);
  const [answeredQuestions, setAnsweredQuestions] = useState(0);
  const [easyCorrectCount, setEasyCorrectCount] = useState(0);
  const [mediumCorrectCount, setMediumCorrectCount] = useState(0);
  const [hardCorrectCount, setHardCorrectCount] = useState(0);
  const [difficulty, setDifficulty] = useState('easy');

  useEffect(() => {
  axios
    .get('http://localhost:8000/api/questions')
    .then((response) => {
      // Filter questions to only include easy questions
      const easyQuestions = response.data.filter(q => q.difficulty === 'easy');
      // Randomly select one of the easy questions
      const selectedQuestion = _.sample(easyQuestions);
      // Set the questions state variable to include only the selected question
      setQuestions([selectedQuestion]);
      setIsLoading(false);
    })
    .catch((error) => {
      console.log(error);
      setIsLoading(false);
    });
}, []);

  useEffect(() => {
    console.log('Current Score:', score);
  }, [score]);

  const handleAnswerOptionClick = (isCorrect, questionDifficulty) => {
    let questionScore = 0;
    if (isCorrect) {
      if (questionDifficulty === 'easy') {
        questionScore = 1;
        setEasyCorrectCount(easyCorrectCount + 1);
      } else if (questionDifficulty === 'medium') {
        questionScore = 2;
        setMediumCorrectCount(mediumCorrectCount + 1);
      } else if (questionDifficulty === 'hard') {
        questionScore = 3;
        setHardCorrectCount(hardCorrectCount + 1);
      }
      setScore(score + questionScore);
    }
    setAnsweredQuestions(answeredQuestions + 1);

    let nextQuestionIndex = currentQuestion + 1;
    let nextDifficulty = difficulty;
    if (difficulty === 'easy' && easyCorrectCount < 2) {
      // Display another easy question
      nextDifficulty = 'easy';
    } else if (difficulty === 'easy' && easyCorrectCount >= 2) {
      // Move on to medium questions
      nextDifficulty = 'medium';
      setMediumCorrectCount(0);
    } else if (difficulty === 'medium' && mediumCorrectCount < 2) {
      // Display another medium question
      nextDifficulty = 'medium';
    } else if (difficulty === 'medium' && mediumCorrectCount >= 2) {
      // Move on to hard questions
      nextDifficulty = 'hard';
      setHardCorrectCount(0);
    } else if (difficulty === 'hard') {
      // Display another hard question
      nextDifficulty = 'hard';
    }

    const nextQuestions = questions.filter(q => q.difficulty === nextDifficulty);
    if (nextQuestionIndex >= nextQuestions.length) {
      // If we've run out of questions at the current difficulty level, move to the next level
      nextDifficulty = nextDifficulty === 'easy' ? 'medium' : 'hard';
      nextQuestionIndex = 0;
    }
    setCurrentQuestion(nextQuestionIndex);

};


 return (
    <div className="quiz">
      {isLoading ? (
        <div className="loading">Loading</div>
      ) : showScore ? (
        <Score
          score={score}
          totalQuestions={questions.length}
          hasPostedResults={hasPostedResults}
          setHasPostedResults={setHasPostedResults}
        />
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/10
            </div>
            <div className="question-text">{questions[currentQuestion].question}</div>
            <div className="difficulty-level">
              Difficulty level: {questions[currentQuestion].difficulty}
            </div>
          </div>
          <div className="answer-section">
            {_.chunk(_.shuffle(questions[currentQuestion].options), 2).map((optionChunk) => (
              <div className="answer-row">
                {optionChunk.map((option) => (
                  <button
                    onClick={() =>
                      handleAnswerOptionClick(
                        option === questions[currentQuestion].answer,
                        questions[currentQuestion].difficulty
                      )
                    }
                  >
                    {option}
                  </button>
                ))}
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default Quiz;
