import React from 'react';
import { Bar, Line, Pie } from 'react-chartjs-2';

// Total Students component
const TotalStudents = ({ total }) => {
  return (
    <div>
      <h2>Total Students: {total}</h2>
    </div>
  );
};

// Pass/Fail component
const PassFail = ({ pass, fail }) => {
  const data = {
    labels: ['Pass', 'Fail'],
    datasets: [
      {
        data: [pass, fail],
        backgroundColor: ['#36A2EB', '#FF6384'],
      },
    ],
  };

  return (
    <div>
      <h2>Pass/Fail</h2>
      <Pie data={data} />
    </div>
  );
};

// Average Score component
const AverageScore = ({ average }) => {
  return (
    <div>
      <h2>Average Score: {average}</h2>
    </div>
  );
};

// Top Performers component
const TopPerformers = ({ performers }) => {
  const data = {
    labels: performers.map((student) => student.name),
    datasets: [
      {
        label: 'Top Performers',
        data: performers.map((student) => student.score),
        backgroundColor: ['#36A2EB', '#FFCE56', '#FF6384', '#4BC0C0', '#9966FF'],
      },
    ],
  };

  return (
    <div>
      <h2>Top Performers</h2>
      <Bar data={data} />
    </div>
  );
};

// Score Distribution component
const ScoreDistribution = ({ scores }) => {
  const data = {
    labels: ['0-20', '20-40', '40-60', '60-80', '80-100'],
    datasets: [
      {
        label: 'Score Distribution',
        data: scores,
        backgroundColor: ['#36A2EB', '#FFCE56', '#FF6384', '#4BC0C0', '#9966FF'],
      },
    ],
  };

  return (
    <div>
      <h2>Score Distribution</h2>
      <Line data={data} />
    </div>
  );
};

// Dashboard component
const Dashboard = () => {
  // Dummy data
  const total = 100;
  const pass = 75;
  const fail = 25;
  const average = 70;
  const performers = [
    { name: 'Alice', score: 90 },
    { name: 'Bob', score: 85 },
    { name: 'Charlie', score: 80 },
    { name: 'Dave', score: 75 },
    { name: 'Eve', score: 70 },
  ];
  const scores = [10, 20, 30, 40, 50];

  return (
    <div>
      <TotalStudents total={total} />
      <PassFail pass={pass} fail={fail} />
      <AverageScore average={average} />
      <TopPerformers performers={performers} />
      <ScoreDistribution scores={scores} />
    </div>
  );
};

export default Dashboard;
