import React, { useState } from 'react';
import _ from 'lodash';
import { Link } from 'react-router-dom'
import './Quiz.css';

import Score from './Score';
import {quizData} from './quizData';



const Quiz = () => {
  const [questions, setQuestions] = useState(_.shuffle(quizData));
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);

  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  const handlePreviousButtonClick = () => {
    const previousQuestion = currentQuestion - 1;
    if (previousQuestion >= 0) {
      setCurrentQuestion(previousQuestion);
    }
  };

  const handleNextButtonClick = () => {
  const nextQuestion = currentQuestion + 1;
  if (nextQuestion < questions.length) {
    setCurrentQuestion(nextQuestion);
    setShowScore(false); // reset showScore state when moving to next question
  } else {
    setShowScore(true); // set showScore state to true only when Finish button is clicked
  }
};


  const restartQuiz = () => {
    setQuestions(_.shuffle(quizData));
    setCurrentQuestion(0);
    setShowScore(false);
    setScore(0);
  };

  return (
    <div className="quiz">
      {showScore ? (
        <Score score={score} totalQuestions={questions.length} restartQuiz={restartQuiz}/>
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{questions.length}
            </div>
            <div className="question-text">{questions[currentQuestion].question}</div>
          </div>
          <div className="answer-section">
            {_.chunk(questions[currentQuestion].options, 2).map((optionChunk) => (
              <div className="answer-row">
                {optionChunk.map((option) => (
                  <button 
                    disabled={showScore}
                    onClick={() => handleAnswerOptionClick(option === questions[currentQuestion].answer)}
                  >
                    {option}
                  </button>
                ))}
              </div>
            ))}
          </div>
          <div className="button-section">
            {currentQuestion > 0 && (
              <button onClick={handlePreviousButtonClick}>Previous</button>
            )}
            {currentQuestion < questions.length - 1 ? (
              <button onClick={handleNextButtonClick}>Next</button>
            ) : (
              <button onClick={handleNextButtonClick}>Finish</button>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Quiz;
