import React from "react";

const Options = ({ question, handleOptionClick }) => {
  return (
    <div className="options">
      {question.options.map((option, index) => (
        <button key={index} onClick={() => handleOptionClick(option)}>
          {option}
        </button>
      ))}
    </div>
  );
};

export default Options;
