const generatePdf = () => {
  const input = document.querySelector('#pdf-content');
  const components = input.children;

  let pdf = new jsPDF();
  let pageHeight = pdf.internal.pageSize.getHeight();
  let yPosition = 10;

  for (let i = 0; i < components.length; i++) {
    const component = components[i];
    html2canvas(component).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const imgProps = pdf.getImageProperties(imgData);
      const pdfWidth = pdf.internal.pageSize.getWidth() - 20;
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      pdf.addImage(imgData, 'PNG', 10, yPosition, pdfWidth, pdfHeight);

      yPosition += pdfHeight + 10;
      if (yPosition > pageHeight - 10) {
        pdf.addPage();
        yPosition = 10;
      }

      if (i === components.length - 1) {
        pdf.save('dashboard.pdf');
      }
    });
  }
};



const Dashboard = () => {
  return (
    <div className="dashboard">

      <div id="pdf-content">
      <h1>Analysis Report</h1>
        <ScoreDistribution />
        <PassFailRate />
        <TopicwiseTopPerformers />
      </div>
      <div className="button-container">
        <Link to="/scoreboard"><button>Back</button></Link>
        <button onClick={generatePdf}>Download PDF</button>
        <Link to="/"><button>Logout</button></Link>
      </div>
    </div>
  );
};

export default Dashboard;
