import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import './StartScreen.css';
import { TeacherContext } from './TeacherContext';

function StartScreen() {
  const { loggedIn } = useContext(TeacherContext);

  return (
    <div className="StartScreen">
      <img src={require('../images/logo.png')} alt="Logo" id="logo" height={200} width={200} ></img>
      <h1>Government of Karnataka</h1>
      <p>A fun way to learn maths!</p>
      <div className="button-container">
        {loggedIn ? (
          <>
            <Link to="/quiz"><button>Quiz</button></Link>
            <Link to="/practice"><button>Practice</button></Link>
          </>
        ) : (
          <>
            <Link to="/teacher-login"><button>Teacher</button></Link>
            <Link to="/student-login"><button>Student</button></Link>
          </>
        )}
      </div>
    </div>
  );
}

export default StartScreen;
