import React, { useState, useEffect } from 'react';
import './styles/Scoreboard.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Scoreboard = () => {
  const [students, setStudents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Make an HTTP GET request to your backend API endpoint
    axios.get('http://localhost:8000/api/result')
      .then(response => {
        // Sort the students array in descending order based on point
        const sortedStudents = response.data.sort((a, b) => b.point - a.point);
        // Set the students state variable to the sorted data retrieved from the API
        setStudents(sortedStudents);
        setIsLoading(false);
      })
      .catch(error => {
        console.log(error);
        setIsLoading(false);
      });
  }, []);

if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }
  return (
    <div className="scoreboard">
      <h1>SCORE BOARD</h1>

        <table>
          <thead>
            <tr>
              <th>S.No.</th>
              <th>Student Name</th>
              <th>Roll Number</th>
              <th>Score</th>
              <th>Result</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student, index) => (
              <tr key={student._id}>
                <td>{index + 1}</td>
                <td>{student.studentName}</td>
                <td>{student.rollNumber}</td>
                <td>{student.point}</td>
                <td>{student.point >= 4 ? (<td className="result-pass">Pass</td>) : (<td className="result-fail">Fail</td>)}</td>
              </tr>
            ))}
          </tbody>
        </table>

      <div className="scoreboard-buttons">
        <Link to="/Teacher">
          <button>Home</button>
        </Link>
        <Link to="/">
          <button>Logout</button>
        </Link>
      </div>
    </div>
  );
};

export default Scoreboard;
