import React, { useState, useEffect } from 'react';
import { VictoryChart, VictoryBar, VictoryAxis, VictoryTooltip } from 'victory';
import axios from 'axios';
import './styles/ScoreDistribution.css';

const ScoreDistribution = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [data, setData] = useState([]);
  const [topics, setTopics] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState("");

  useEffect(() => {
    axios.get('http://localhost:8000/api/result')
      .then(response => {
        const scoresByTopic = groupScoresByTopic(response.data);
        const histogramData = calculateHistogramData(scoresByTopic);
        setData(histogramData);
        setIsLoading(false);
      })
      .catch(error => {
        console.log(error);
        setIsLoading(false);
      });

    fetchTopics();
  }, []);

  const groupScoresByTopic = (data) => {
    const scoresByTopic = {};
    for (const student of data) {
      const { topic, point } = student;
      if (!scoresByTopic[topic]) {
        scoresByTopic[topic] = [];
      }
      scoresByTopic[topic].push(point);
    }
    return scoresByTopic;
  };

  const fetchTopics = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/result/topic');
      console.log(response.data); // Check the response data
      setTopics(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleTopicSelect = (event) => {
    setSelectedTopic(event.target.value);
  }

 const calculateHistogramData = (scoresByTopic) => {
  const bins = [0, 5, 10, 15, 20, 25];
  const histogramData = [];

  for (const topic in scoresByTopic) {
    if (selectedTopic !== "" && topic !== selectedTopic) {
      continue;
    }

    const binCounts = new Array(bins.length - 1).fill(0);
    const scores = scoresByTopic[topic];

    // Filter scores by selected topic
    if (selectedTopic !== "") {
      scores = scores.filter((score) => score.topic === selectedTopic);
    }

    for (const score of scores) {
      for (let i = 1; i < bins.length; i++) {
        if (score >= bins[i - 1] && score < bins[i]) {
          binCounts[i - 1]++;
          break;
        }
      }
    }

    const topicHistogramData = binCounts.map((count, i) => ({
      x: `${bins[i]}-${bins[i + 1]}`,
      y: count,
      topic,
    }));
    histogramData.push(...topicHistogramData);
  }

  return histogramData;
};


  if (isLoading) {
    return (
      <div className="loading">
        <p>Loading</p>
      </div>
    );
  }

  if (data.length === 0) {
    return (
      <div className="score-distribution-empty">
        <h1>SCORE DISTRIBUTION</h1>
        <p>No scores available to display</p>
      </div>
    );
  }

return (
  <div className="score-distribution">
    <h1>SCORE DISTRIBUTION</h1>
    <div className="topic-selector">
      <label htmlFor="topic">Select a topic:</label>
      <select id="topic" value={selectedTopic} onChange={handleTopicSelect}>
        <option value="">All Topics</option>
        {topics.map((topic) => (
          <option key={topic}>{topic}</option>
        ))}
      </select>
    </div>
    <div className="chart-container">
      <VictoryChart
        domainPadding={20}
        animate={{ duration: 500, easing: "bounce" }}
      >
        <VictoryAxis
          label="Score Range"
          tickFormat={(x) => `${x}`}
        />
        <VictoryAxis
          dependentAxis
          label="Number of Students"
          tickFormat={(y) => `${y}`}
        />
        <VictoryBar
          data={data}
          x="x"
          y="y"
          labels={({ datum }) => `${datum.topic}: ${datum.y}`}
          labelComponent={<VictoryTooltip />}
        />
      </VictoryChart>
    </div>
  </div>
);

};

export default ScoreDistribution;
