import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './StudentLogin.css';

function StudentLogin() {
  const [isLogin, setIsLogin] = useState(true);
  const [name, setName] = useState('');
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleIdChange = (event) => {
    setId(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleConfirmPasswordChange = (event) => {
    setConfirmPassword(event.target.value);
  };

  const handleSignup = () => {
    // TODO: Implement signup logic
    if (password === confirmPassword) {
      setMessage(`Account created successfully. Please login.`);
    } else {
      setMessage(`Passwords do not match.`);
    }
  };

  const handleLogin = () => {
    // TODO: Implement login logic
  };

  const toggleForm = () => {
    setIsLogin(!isLogin);
    setMessage('');
  };

  return (
    <div className="wrapper">
      <form className="form">
        <h1 className="title">{isLogin ? 'Login' : 'Signup'}</h1>
        <div className="input-group">
          <label className="label">Name:</label>
          <input className="input" type="text" value={name} onChange={handleNameChange} />
        </div>
        {!isLogin && (
          <div className="input-group">
            <label className="label">ID:</label>
            <input className="input" type="text" value={id} onChange={handleIdChange} />
          </div>
        )}
        <div className="input-group">
          <label className="label">Password:</label>
          <input className="input" type="password" value={password} onChange={handlePasswordChange} />
        </div>
        {!isLogin && (
          <div className="input-group">
            <label className="label">Confirm Password:</label>
            <input className="input" type="password" value={confirmPassword} onChange={handleConfirmPasswordChange} />
          </div>
        )}
        <button className="button" type="button" onClick={isLogin ? handleLogin : handleSignup}>
          {isLogin ? 'Login' : 'Signup'}
        </button>
        {message && (
          <p className="message">{message} <Link to="#" onClick={toggleForm}>{isLogin ? 'Signup' : 'Login'}</Link></p>
        )}
        {!message && isLogin ? (
          <p className="message">
            Don't have an account? <Link to="#" onClick={toggleForm}>Signup</Link>
          </p>
        ) : (
          <p className="message">
            Already have an account? <Link to="#" onClick={toggleForm}>Login</Link>
          </p>
        )}
      </form>
    </div>
  );
}

export default StudentLogin;
