import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './styles/StudentLogin.css';

function StudentLogin() {
  const [rollNumber, setRollNumber] = useState('');
  const [studentName, setStudentName] = useState('');
  const [rollNumberWarning, setRollNumberWarning] = useState('');
  const [studentNameWarning, setStudentNameWarning] = useState('');
  const history = useHistory();

  const handleRollNumberChange = (event) => {
    const value = event.target.value;
    if (value === '' || /^[0-9]+$/.test(value)) {
      setRollNumber(value);
      setRollNumberWarning('');
    } else {
      setRollNumberWarning('Please enter only numbers');
    }
  };

  const handleStudentNameChange = (event) => {
    const value = event.target.value;
    if (value === '' || /^[a-zA-Z]+$/.test(value)) {
      setStudentName(value);
      setStudentNameWarning('');
    } else {
      setStudentNameWarning('Please enter only letters');
    }
  };

  const handleLogin = () => {
    if (rollNumber.trim() === '') {
      setRollNumberWarning('Please enter a roll number');
    } else if (studentName.trim() === '') {
      setStudentNameWarning('Please enter a student name');
    } else if (rollNumberWarning !== '' || studentNameWarning !== '') {
      // Do nothing, invalid input warning already displayed
    } else {
      // Login successful, redirect to student homepage
      history.push(`/quiz?studentName=${encodeURIComponent(studentName)}&rollNumber=${encodeURIComponent(rollNumber)}`);
    }
  };

  return (
    <div className="wrapper">
      <form className="form">
        <h1 className="title">Login</h1>
        <div className="input-group">
          <label className="label">Roll Number:</label>
          <input className="input" type="text" value={rollNumber} onChange={handleRollNumberChange} />
          {rollNumberWarning && <div className="warning">{rollNumberWarning}</div>}
        </div>
        <div className="input-group">
          <label className="label">Student Name:</label>
          <input className="input" type="text" value={studentName} onChange={handleStudentNameChange} />
          {studentNameWarning && <div className="warning">{studentNameWarning}</div>}
        </div>
        <button className="button" type="button" onClick={handleLogin}>
          Attempt Quiz !
        </button>
      </form>
    </div>
  );
}

export default StudentLogin;
